#|
use strict;
my $middle_code = "";

sub decode {
	my ($string_to_decode) = @_;
	my @array = ($string_to_decode =~ m/../g);
	my @decoded_array = map { chr(hex($_)) } @array;
    return join('', @decoded_array);
}

printf "%s%s%s", "#\|\nuse strict;\nmy \$middle_code = \"", $middle_code, "\";\n";
printf "%s", decode($middle_code);
printf "%s", "\n(define middle_code \"$middle_code\")\n";
printf "%s", "(display (hex-string->bytes middle_code))\n"

__END__
|#
#lang racket
(require file/sha1)
(define middle_code "")
(display (hex-string->bytes middle_code))
